__ht__function(g, rect, comp, data, view) {
var x = rect.x;
    var y = rect.y;
    var width = rect.width;
    var height = rect.height;

    var borderColor = comp.getColor('borderColor');
    var borderWidth = comp.getValue('borderWidth');
    var background = comp.getColor('backgroundColor');
    var barBGColor = comp.getColor('barBGColor');
    var barColor = comp.getColor('barColor');
    var paddingTop = comp.getValue('paddingTop');
    var paddingBottom = comp.getValue('paddingBottom');
    var paddingLeft = comp.getValue('paddingLeft');
    var paddingRight = comp.getValue('paddingRight');
    var gapCount = comp.getValue('gapCount');
    var gapWidth = comp.getValue('gapWidth');
    var labelText = comp.getValue('labelText');
    var labelColor = comp.getColor('labelColor');
    var fontSize = comp.getValue('fontSize');
    var labelX = comp.getValue('labelX');
    var labelY = comp.getValue('labelY');
    var percentValue = comp.getValue('percentValue');

    if (paddingLeft > 100) paddingLeft = 100;
    if (paddingTop > 100) paddingTop = 100;
    var barX = paddingLeft * width / 100 + x;
    var barY = paddingTop * height / 100 + y;
    var barW = Math.max((100 - paddingLeft - paddingRight) * width / 100, 0);
    var barH = Math.max((100 - paddingTop - paddingBottom) * height / 100, 0);

    if (percentValue < 0) percentValue = 0;
    if (percentValue > 1) percentValue = 1;

    if (background) {
        g.fillStyle = background;
        g.beginPath();
        g.rect(x, y, width, height);
        g.fill();
    }

    var cellWidth = barW;
    if (gapCount * gapWidth > barW) {
        gapCount = 0;
    }
    else {
        cellWidth = (barW - gapCount * gapWidth) / (gapCount + 1);
    }
    if (gapCount == 0) {
        gapWidth = 0;
    }

    if (barBGColor && percentValue < 1) {
        for (var i = 0; i < gapCount + 1; i++) {
            g.fillStyle = barBGColor;
            g.beginPath();
            g.rect(barX + i * (cellWidth + gapWidth), barY, cellWidth, barH);
            g.fill();
        }
    }

    if (barColor && percentValue > 0) {
        var cellCount = Math.floor(barW * percentValue / (cellWidth + gapWidth));
        for (var i = 0; i < cellCount; i++) {
            g.fillStyle = barColor;
            g.beginPath();
            g.rect(barX + i * (cellWidth + gapWidth), barY, cellWidth, barH);
            g.fill();
        }
        var lastCellW = barW * percentValue - (cellWidth + gapWidth) * cellCount;
        if (lastCellW > 0) {
            if (lastCellW > cellWidth) {
                lastCellW = cellWidth;
            }
            g.fillStyle = barColor;
            g.beginPath();
            g.rect(barX + cellCount * (cellWidth + gapWidth), barY, lastCellW, barH);
            g.fill();
        }
    }

    if (borderWidth && borderColor) {
        ht.Default.drawBorder(g, borderColor, barX, barY, barW, barH, borderWidth);
    }

    if (labelText) {
        ht.Default.drawText(g, labelText, fontSize + 'px arial, sans-serif', labelColor, x, y, width, height, labelX, labelY);
    }
}
