image.onUp:
__ht__function(event, data, view, point, width, height) {
if(!data.ca('disabled')){
    data.ca('icon-background',data.ca('hover-background'));
    let cb = new Function('return ' + data.ca('clicked'))();
    typeof(setBindTabviewIndex) != 'undefined' && setBindTabviewIndex(data,data.ca('bindTabview'));
    let paramCb = new Function('return ' + data.ca('param'))();
    let param = paramCb ? paramCb(data,view) : null;
    typeof(i) != 'undefined' && typeof(i.updateBindControls) != 'undefined' && i.updateBindControls(data,param,[],false,'~','clicked');//params是response的位置，所以得是对象类型\
    return cb && cb(data,param)
}
}

image.onEnter:
__ht__function(event, data, view, point, width, height) {
if(data.ca('disabled')){
    view.getView().style.cursor = 'not-allowed'
}else{
    data.ca('icon-background',data.ca('hover-background'))
    data.iv();
    view.getView().style.cursor = 'pointer'
}
}

image.onLeave:
__ht__function(event, data, view, point, width, height) {
data.ca('icon-background',data.ca('default-background'))
view.getView().style.cursor = 'default'
}

name.func:
__ht__function(data, view) {
function func(data,view){
    if((typeof(runningMode) != 'undefined' && !runningMode()) || view.getView().style.cursor != 'pointer'){//最好不用 == 'default'，因为存在代码初始赋值
        data.ca('icon-background',data.ca('default-background'))
    }
    var typeArr = ['light','light','regular','light'] //枚举下拉选择
    var typetmp = typeArr[data.ca('iconType')] //类型选择
    var path = data.ca('path')                 //图标路径
    if(path != undefined){
        if(path.indexOf('.') == -1){           //传入图标文件名称时
            path = 'symbols/demo/uiotos/icons/' + typetmp + '/' + path + '.json'
        }else if(path.indexOf(typetmp) == -1){ //图标类型发生变化时
            typeArr.forEach((curType,index)=>{
                if(curType != typetmp){        //路径中先前的类型名称换成新的
                    path = path.replace(curType,typetmp)               
                }
            })
        }
    }
    data.ca('path',path)                      //回写过去
    return path
}

//整体复制的话需要带上这句，因为属性func外层已经是函数体
return func(data,view)
}

